import React, { useState, useRef, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { configurarAnimacoesUpload } from "./animations.js";
import "./upload.css"; 
import {saveAs} from "file-saver"
import axios from "axios";

const UploadDocument = () => {
  const [selectedFiles, setSelectedFiles] = useState([]);
  const [error, setError] = useState(null);
  const inputRef = useRef(null);
  const [dragOver, setDragOver] = useState(false);

  const validFormats = ["image/jpeg", "image/png", "image/bmp"];
  const maxWidth = 2048;
  const maxHeight = 2048;

  //para fazer download da imagem com watermark
  const [loading, setLoading] = useState(false);


  const navigate = useNavigate();
  
  useEffect(() => {
    const token = sessionStorage.getItem("access_token");
    if (!token) {
      navigate("/");
    } else {
      configurarAnimacoesUpload();
    }
  }, [navigate]);

  const logImageAction = async ({ action, fileName, status, message }) => {
  const nif = sessionStorage.getItem("nif");
  if (!nif) {
    console.warn("NIF não encontrado na sessão.");
    return;
  }

  try {
    await fetch("http://localhost:5000/log-image-action", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        "Authorization": `Bearer ${sessionStorage.getItem("access_token")}`
      },
      body: JSON.stringify({
        nif,
        action,
        fileName: fileName || "desconhecido",
        status,
        message,
      }),
    });
  } catch (err) {
    console.error("Erro ao registar log:", err);
  }
};


  const handleFileChange = (e) => {
    setError(null);
    const newFiles = Array.from(e.target.files);
    if (newFiles.length === 0) return;

    let pendingValidations = newFiles.length;
    const validNewFiles = [];

    newFiles.forEach((file) => {
      if (!validFormats.includes(file.type)) {
        const msg = `Formato inválido (${file.name}). Só JPG, PNG e BMP são permitidos.`;
        setError(msg);
        logImageAction({
          action: "Upload",
          fileName: file.name,
          status: "Erro",
          message: msg,
        });
        pendingValidations--;
        if (pendingValidations === 0)
          setSelectedFiles((prev) => [...prev, ...validNewFiles]);
        return;
      }

      const img = new Image();
      img.onload = () => {
        if (img.width > maxWidth || img.height > maxHeight) {
          const msg = `Imagem ${file.name} excede ${maxWidth}x${maxHeight} px.`;
          setError(msg);
          logImageAction({
            action: "Upload",
            fileName: file.name,
            status: "Erro",
            message: msg,
          });
        } else {
          validNewFiles.push(file);
          logImageAction({
            action: "Upload",
            fileName: file.name,
            status: "Sucesso",
            message: "Imagem validada e adicionada.",
          });
        }
        pendingValidations--;
        if (pendingValidations === 0)
          setSelectedFiles((prev) => [...prev, ...validNewFiles]);
      };
      img.onerror = () => {
        const msg = `Erro ao carregar imagem ${file.name}.`;
        setError(msg);
        logImageAction({
          action: "Upload",
          fileName: file.name,
          status: "Erro",
          message: msg,
        });
        pendingValidations--;
        if (pendingValidations === 0)
          setSelectedFiles((prev) => [...prev, ...validNewFiles]);
      };
      img.src = URL.createObjectURL(file);
    });

    if (inputRef.current) {
      inputRef.current.value = null;
    }
  };

  const handleRemoveImage = (indexToRemove) => {
    const file = selectedFiles[indexToRemove];
    logImageAction({
      action: "Remoção",
      fileName: file.name,
      status: "Sucesso",
      message: "Imagem removida pelo utilizador.",
    });

    setSelectedFiles((prev) =>
      prev.filter((_, idx) => idx !== indexToRemove)
    );
  };


  /* ESTE
  const handleApplyWatermark = async () => {
    if (selectedFiles.length === 0) {
      alert("Nenhum ficheiro selecionado.");
      return;
    }

    const token = sessionStorage.getItem("access_token");
    const formData = new FormData();
    formData.append("file", selectedFiles[0]);

    try {
      const response = await axios.post("http://localhost:5000/predict", formData, {
        headers: {
          Authorization: `Bearer ${token}`,
          "Content-Type": "multipart/form-data",
        },
        responseType: "blob", 
      });

      // Criar blob e forçar o download
      const blob = new Blob([response.data], { type: "image/jpeg" });
      saveAs(blob, `watermarked-${selectedFiles[0].name}`);
      alert("Watermark aplicada com sucesso!");
    } catch (error) {
      console.error("Erro ao aplicar watermark:", error);
      alert("Erro ao processar a imagem.");
    }
  };
*/

  const handleApplyWatermark = async () => {
  if (selectedFiles.length === 0) {
    alert("Nenhum ficheiro selecionado.");
    return;
  }

  const token = sessionStorage.getItem("access_token");
  const formData = new FormData();
  formData.append("file", selectedFiles[0]);

  try {
    const response = await axios.post("http://localhost:5000/predict", formData, {
      headers: {
        Authorization: `Bearer ${token}`,
        // NÃO definir Content-Type, axios faz isso automático
      },
      responseType: "blob",
    });

    // Forçar download do ficheiro fixo "0.jpeg"
    saveAs(response.data, "0.jpeg");

    alert("Watermark aplicada com sucesso!");
  } catch (error) {
    console.error("Erro ao aplicar watermark:", error);
    alert("Erro ao processar a imagem.");
  }
};


  const handleDrop = (e) => {
    e.preventDefault();
    setDragOver(false);
    handleFileChange({ target: { files: e.dataTransfer.files } });
  };

  const handleLogout = () => {
    sessionStorage.removeItem("access_token");
    sessionStorage.removeItem("nif");
    navigate("/");
  };

  return (
  <div>
    <div className="header">
      <div className="logo">
          <img src="logo-transparent.png" alt="logotipo" className="logo-img" onClick={() => navigate('/homepage')} style={{ cursor: 'pointer' }}/>
      </div>
        <button
        className="logout-button-upload"
        onClick={handleLogout}
      >
        Sair
      </button>
    </div>
        <br></br>
    <div className="upload-wrapper">      
      <h2 className="title">Inserir marca d'água</h2>
      <div className="upload-container">
        <div
          className={`drop-zone ${dragOver ? "dragover" : ""}`}
          onDragOver={(e) => {
            e.preventDefault();
            setDragOver(true);
          }}
          onDragLeave={() => setDragOver(false)}
          onDrop={handleDrop}
        >
          <p className="small-text">Envie arquivos para aplicar marca d'água</p>
          <h3>Arraste e Solte os arquivos aqui</h3>
          <p>Ou</p>
          <label className="upload-btn">
            <span className="plus">+</span> Carregar
            <input
              type="file"
              multiple
              ref={inputRef}
              onChange={handleFileChange}
              style={{ display: "none" }}
              accept=".jpg,.jpeg,.png,.bmp"
            />
          </label>
          <p className="file-types">
            <strong>Suporta ficheiros do tipo</strong><br />
            jpeg, png, bmp
          </p>
        </div>

        <div className="illustration">
          <img src="/imgUpload-1.png" alt="Upload Illustration" />
        </div>
      </div>

      {error && <div className="error">{error}</div>}

      {selectedFiles.length > 0 && (
        <>
          <ul className="file-list">
            {selectedFiles.map((file, index) => (
              <li key={index}>
                {file.name}
                <button
                  className="remove-btn"
                  onClick={() => handleRemoveImage(index)}
                >
                  &times;
                </button>
              </li>
            ))}
          </ul>

          <button className="apply-btn" onClick={handleApplyWatermark}>
            Aplicar
          </button>
        </>
      )}
    </div>
  </div>
  );
};

export default UploadDocument;
