import React, { useState, useRef, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { configurarAnimacoesUpload } from "./animations.js";
import "./upload.css"; 
import {saveAs} from "file-saver"
import axios from "axios";

const UploadDocument = () => {
  const [selectedFiles, setSelectedFiles] = useState([]);
  const [error, setError] = useState(null);
  const inputRef = useRef(null);
  const [dragOver, setDragOver] = useState(false);

  const validFormats = ["image/jpeg", "image/png", "image/bmp"];
  const maxWidth = 2048;
  const maxHeight = 2048;

  //para fazer download da imagem com watermark
  const [loading, setLoading] = useState(false);


  const navigate = useNavigate();
  
  useEffect(() => {
    const token = sessionStorage.getItem("access_token");
    if (!token) {
      navigate("/");
    } else {
      configurarAnimacoesUpload();
    }
  }, [navigate]);

  const logImageAction = async ({ action, fileName, status, message }) => {
  const nif = sessionStorage.getItem("nif");
  if (!nif) {
    console.warn("NIF não encontrado na sessão.");
    return;
  }

  try {
    await fetch("http://localhost:5000/log-image-action", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        "Authorization": `Bearer ${sessionStorage.getItem("access_token")}`
      },
      body: JSON.stringify({
        nif,
        action,
        fileName: fileName || "desconhecido",
        status,
        message,
      }),
    });
  } catch (err) {
    console.error("Erro ao registar log:", err);
  }
};


  const handleFileChange = (e) => {
    setError(null);
    const newFiles = Array.from(e.target.files);
    if (newFiles.length === 0) return;

    let pendingValidations = newFiles.length;
    const validNewFiles = [];

    newFiles.forEach((file) => {
      if (!validFormats.includes(file.type)) {
        const msg = `Formato inválido (${file.name}). Só JPG, PNG e BMP são permitidos.`;
        setError(msg);
        logImageAction({
          action: "Upload",
          fileName: file.name,
          status: "Erro",
          message: msg,
        });
        pendingValidations--;
        if (pendingValidations === 0)
          setSelectedFiles((prev) => [...prev, ...validNewFiles]);
        return;
      }

      const img = new Image();
      img.onload = () => {
        if (img.width > maxWidth || img.height > maxHeight) {
          const msg = `Imagem ${file.name} excede ${maxWidth}x${maxHeight} px.`;
          setError(msg);
          logImageAction({
            action: "Upload",
            fileName: file.name,
            status: "Erro",
            message: msg,
          });
        } else {
          validNewFiles.push(file);
          logImageAction({
            action: "Upload",
            fileName: file.name,
            status: "Sucesso",
            message: "Imagem validada e adicionada.",
          });
        }
        pendingValidations--;
        if (pendingValidations === 0)
          setSelectedFiles((prev) => [...prev, ...validNewFiles]);
      };
      img.onerror = () => {
        const msg = `Erro ao carregar imagem ${file.name}.`;
        setError(msg);
        logImageAction({
          action: "Upload",
          fileName: file.name,
          status: "Erro",
          message: msg,
        });
        pendingValidations--;
        if (pendingValidations === 0)
          setSelectedFiles((prev) => [...prev, ...validNewFiles]);
      };
      img.src = URL.createObjectURL(file);
    });

    if (inputRef.current) {
      inputRef.current.value = null;
    }
  };

  const handleRemoveImage = (indexToRemove) => {
    const file = selectedFiles[indexToRemove];
    logImageAction({
      action: "Remoção",
      fileName: file.name,
      status: "Sucesso",
      message: "Imagem removida pelo utilizador.",
    });

    setSelectedFiles((prev) =>
      prev.filter((_, idx) => idx !== indexToRemove)
    );
  };

  /*
  const handleApplyWatermark = () => {
    alert("Watermark aplicada com sucesso!");

    selectedFiles.forEach((file) => {
      const url = URL.createObjectURL(file);
      const link = document.createElement("a");
      link.href = url;
      link.download = `watermarked-${file.name}`;
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
      URL.revokeObjectURL(url);
    });
  };*/

  //alteração para integrar com o modelo
  /*
  const handleApplyWatermark = async () => {
  if (selectedFiles.length === 0) return;

  const results = [];

  for (const file of selectedFiles) {
    const formData = new FormData();
    formData.append("file", file);

    try {
      const response = await fetch("http://localhost:5000/predict", {
        method: "POST",
        headers: {
          Authorization: `Bearer ${sessionStorage.getItem("access_token")}`
        },
        body: formData,
      });

      const data = await response.json();

      if (response.ok) {
        results.push({ file: file.name, result: data.result });
        logImageAction({
          action: "Inferência",
          fileName: file.name,
          status: "Sucesso",
          message: "Marca d'água aplicada com sucesso.",
        });
      } else {
        logImageAction({
          action: "Inferência",
          fileName: file.name,
          status: "Erro",
          message: data.error || "Erro desconhecido na inferência.",
        });
      }
    } catch (error) {
      console.error(`Erro ao enviar ${file.name}:`, error);
      logImageAction({
        action: "Inferência",
        fileName: file.name,
        status: "Erro",
        message: error.message,
      });
    }
  }

  alert("Processamento concluído!");
  console.log("Resultados:", results);
};*/

