const handleApplyWatermark = async () => {
  if (selectedFiles.length !== 1) {
    setError({message: "Seleciona apenas 1 imagem para este tipo de marca d'água.", type: "error"});
    return;
  }

  const file = selectedFiles[0];
  const base64 = await fileToBase64(file);
  const token = sessionStorage.getItem("access_token");

  setLoading(true);

  try {
    const response = await fetch("http://localhost:5000/infer-torchserve", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${token}`
      },
      body: JSON.stringify({
        image: base64,
        secret: secretText
      })
    });

    if (!response.ok) throw new Error("Erro na inferência");

    const result = await response.json();

    // Baixar a imagem estego
    const link = document.createElement("a");
    link.href = "data:image/png;base64," + result.stego_image_base64;
    link.download = "imagem_com_segredo.png";
    document.body.appendChild(link);
    link.click();
    link.remove();

    setError({message: "Marca d'água aplicada com sucesso!", type: "success"});
    logImageAction({
      action: "Marca d'água",
      fileName: file.name,
      status: "Sucesso",
      message: "Imagem processada via TorchServe"
    });

  } catch (err) {
    setError({message: "Erro ao aplicar a marca d'água.", type: "error"});
  } finally {
    setLoading(false);
  }
};




  const handleDrop = (e) => {
