import { useNavigate } from "react-router-dom";

function Error404() {
  const navigate = useNavigate();

  fetch("/api/endpoint")
    .then(async (res) => {
      if (!res.ok) {
        if (res.status === 403) {
          navigate("/403"); // rota React para página 403
        } else if (res.status === 404) {
          navigate("/404"); // rota React para página 404
        } else if (res.status === 500) {
          navigate("/500"); // rota React para página 500
        } else {
          const errorData = await res.json();
          alert("Erro: " + errorData.error);
        }
        throw new Error("Erro na requisição");
      }
      return res.json();
    })
    .then(data => {
      // trata resposta normal
    })
    .catch(err => {
      console.error(err);
    });
}


const styles = {
  container: {
    height: "100vh",
    display: "flex",
    flexDirection: "column",
    justifyContent: "center",
    alignItems: "center",
    backgroundColor: "#f5f5f5",
    fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
    padding: "20px",
  },
  title: {
    fontSize: "8rem",
    margin: 0,
    color: "#e74c3c",
  },
  message: {
    fontSize: "1.5rem",
    marginBottom: "2rem",
    color: "#333",
  },
  button: {
    padding: "12px 24px",
    fontSize: "1rem",
    backgroundColor: "#3498db",
    color: "#fff",
    border: "none",
    borderRadius: "6px",
    cursor: "pointer",
    transition: "background-color 0.3s ease",
  },
};

export default Error404;
